import { useState } from "react";

export default function Todo() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [todos, setTodos] = useState([
    {
      id: 0,
      title: "",
      description: "",
    },
  ]);

  function handleClick() {
    const newTodos = [
      {
        id: todos[todos.length - 1].id + 1,
        title: title,
        description: description,
      },
    ];
    setTodos([...todos, ...newTodos]);
  }

  return (
    <>
      <div classNameName="mx-3 my-4">
        <form
          onSubmit={(e) => {
            e.preventDefault();
          }}
        >
          {/* Title  */}
          <div classNameName="mb-3">
            <label htmlFor="title" classNameName="form-label">
              Title
            </label>
            <input
              type="text"
              classNameName="form-control"
              id="title"
              aria-describedby="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </div>

          {/* Description  */}
          <div classNameName="mb-3">
            <label htmlFor="description" classNameName="form-label">
              Description
            </label>
            <input
              type="text"
              classNameName="form-control"
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>

          {/* Add Todo  */}
          <button
            type="button"
            classNameName="btn btn-outline-primary"
            onClick={handleClick}
          >
            Add Todo
          </button>
        </form>
      </div>
      <div>
        {todos.length !== 0
          ? todos.map((todo) => {
              return (
                <div className="col">
                  <div className="card mb-4 rounded-3 shadow-sm">
                    <div className="card-header py-3">
                      <h4 className="my-0 fw-normal"> {todo.title} </h4>
                    </div>
                    <div className="card-body">
                      <h1 className="card-title pricing-card-title">
                        $0
                        <small className="text-body-secondary fw-light">
                          /mo
                        </small>
                      </h1>
                      <ul className="list-unstyled mt-3 mb-4">
                        <li>10 users included</li>
                      </ul>
                      <button
                        type="button"
                        className="w-100 btn btn-lg btn-outline-primary"
                      >
                        Sign up for free
                      </button>
                    </div>
                  </div>
                </div>
              );
            })
          : alert("Todo is empty")}
      </div>
    </>
  );
}
