import { useState } from "react";

export default function Header({forlabel, inputlabel, inputtype}) {
    const [todoInput, setTodoInput] = useState('')

  return (
    <>
      <div className="mb-3 row mx-3 my-4 border border-dark-subtle">

        <label htmlFor="email" className="col-sm-2 col-form-label border border-dark-subtle">
            {inputlabel}
        </label>
        <div className="col-sm-10">
          <input
            type= {inputtype}
            className="form-control-plaintext"
            id = {forlabel}
            value = {todoInput}
          />
        </div>
      </div>
    </>
  );
}
